import React, {useContext} from "react";
// import Headroom from "react-headroom"; // REMOVED
import "./Header.scss";
import ToggleSwitch from "../ToggleSwitch/ToggleSwitch";
import StyleContext from "../../contexts/StyleContext";
import {
  greeting,
  skillsSection,
  educationInfo,
  bigProjects,
  achievementSection,
} from "../../portfolio";

function Header() {
  const {isDark} = useContext(StyleContext);
  const viewSkills = skillsSection.display;
  const viewEducation = educationInfo.display;
  const viewProjects = bigProjects.display;
  const viewAchievement = achievementSection.display;

  return (
    // Fixed wrapper div is retained
    <div className="header-fixed-wrapper">
      <header className={isDark ? "dark-menu header" : "header"}>
        <a href="/" className="logo">
          <span className="grey-color"> &lt;</span>
          <span className="logo-name">{greeting.username}</span>
          <span className="grey-color">/&gt;</span>
        </a>
        <input className="menu-btn" type="checkbox" id="menu-btn" />
        <label
          className="menu-icon"
          htmlFor="menu-btn"
          style={{color: "white"}}
        >
          <span className={isDark ? "navicon navicon-dark" : "navicon"}></span>
        </label>
        <ul className={isDark ? "dark-menu menu" : "menu"}>

          {/* 1. ABOUT ME (Now links to the #skills section) */}
          {viewEducation && viewSkills && (
            <li>
              <a href="#skills">About Me</a> 
            </li>
          )}
          {/* REMOVED original "Skills" link */}

          {/* 2. PROJECTS */}
          {viewProjects && (
            <li>
              <a href="#projects">Projects</a>
            </li>
          )}
          {/* 3. ACCOMPLISHMENTS */}
          {viewAchievement && (
            <li>
              <a href="#achievements">Accomplishments</a>
            </li>
          )}
          {/* 4. CONTACT ME */}
          <li>
            <a href="#contact">Contact Me</a>
          </li>
          <li>
            {/* Dark Mode Toggle */}
            <a>
              <ToggleSwitch />
            </a>
          </li>
        </ul>
      </header>
    </div>
  );
}
export default Header;
