import React, { useState, useEffect } from "react";
import Header from "../components/header/Header";
import Greeting from "./greeting/Greeting";
import Skills from "./skills/Skills";
// import StackProgress from "./skillProgress/skillProgress"; // We removed this
import Education from "./education/Education";
// import WorkExperience from "./workExperience/WorkExperience"; // We removed this
import Projects from "./projects/Projects"; // This is for Open Source (GitHub)
import StartupProject from "./StartupProjects/StartupProject"; // This is for 'Big Projects'
import Achievement from "./achievement/Achievement";
// import Blogs from "./blogs/Blogs"; // This is set to display: false
// import Talks from "./talks/Talks"; // This is set to display: false
// import Podcast from "./podcast/Podcast"; // This is set to display: false
import Footer from "../components/footer/Footer";
import Profile from "./profile/Profile"; // This is the contact section
import ScrollToTopButton from "./topbutton/Top";
// import Twitter from "./twitter-embed/twitter"; // This is set to display: false
import SplashScreen from "./splashScreen/SplashScreen";
import { splashScreen } from "../portfolio";
import { StyleProvider } from "../contexts/StyleContext";
import { useLocalStorage } from "../hooks/useLocalStorage";
import "./Main.scss";

const Main = () => {
  const darkPref = window.matchMedia("(prefers-color-scheme: dark)");
  const [isDark, setIsDark] = useLocalStorage("isDark", darkPref.matches);
  const [isShowingSplashAnimation, setIsShowingSplashAnimation] = useState(true);

  useEffect(() => {
    if (splashScreen.enabled) {
      const splashTimer = setTimeout(
        () => setIsShowingSplashAnimation(false),
        splashScreen.duration
      );
      return () => {
        clearTimeout(splashTimer);
      };
    }
  }, []);

  const changeTheme = () => {
    setIsDark(!isDark);
  };

  return (
    <div className={isDark ? "dark-mode" : null}>
      <StyleProvider value={{ isDark: isDark, changeTheme: changeTheme }}>
        {isShowingSplashAnimation && splashScreen.enabled ? (
          <SplashScreen />
        ) : (
          <>
            <Header />
            <Greeting />
            <Skills />
            {/* <StackProgress /> */} {/* REMOVED */}
            <Education />
            {/* <WorkExperience /> */} {/* REMOVED */}
            <Projects /> {/* This is the one we still need to fix in portfolio.js */}
            <StartupProject />
            <Achievement />
            {/* <Blogs /> */} {/* REMOVED */}
            {/* <Talks /> */} {/* REMOVED */}
            {/* <Twitter /> */} {/* REMOVED */}
            {/* <Podcast /> */} {/* REMOVED */}
            <Profile />
            <Footer />
            <ScrollToTopButton />
          </>
        )}
      </StyleProvider>
    </div>
  );
};

export default Main;
